INSERT INTO `procs` VALUES (33,'Afficher l\'historique des transaction sur une date précise','SELECT IFNULL(e.empr_nom,\'utilisateur\') as \'Nom\', IFNULL(e.empr_prenom,\'supprim\') as \'prnom\',t.id_transaction as \'numro de transaction\',t.date_prevue AS date, ( t.montant * t.sens ) AS montant, t.commentaire \r\nFROM transactions t\r\nLEFT OUTER JOIN comptes c on t.compte_id = c.id_compte \r\nLEFT OUTER JOIN empr e on  c.proprio_id = e.id_empr\r\nWHERE t.date_prevue = \'!!param1!!\' \r\nORDER BY t.date_prevue, sens, t.id_transaction','Affichage du numéro de la transaction, de la date et du montant','12 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n</FIELDS>',4,0,''),(34,'Afficher l\'historique des transactions entre deux dates.','SELECT IFNULL(e.empr_nom,\'utilisateur\') as \'Nom\', IFNULL(e.empr_prenom,\'supprim\') as \'prnom\',t.id_transaction as \'numro de transaction\',t.date_prevue AS date, ( t.montant * t.sens ) AS montant, t.commentaire \r\nFROM transactions t\r\nLEFT OUTER JOIN comptes c on t.compte_id = c.id_compte \r\nLEFT OUTER JOIN empr e on  c.proprio_id = e.id_empr\r\nWHERE t.date_prevue >= \'!!param1!!\' AND t.date_prevue <= \'!!param2!!\'\r\nORDER BY t.date_prevue, sens, t.id_transaction','Affichage du numéro de la transaction, de la date et du montant','12 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date de début]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n <FIELD NAME=\"param2\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date de fin]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n</FIELDS>',4,0,''),(35,'Exemplaires prix.','SELECT IFNULL(n.tit1,\'notice introuvable\') as \"notice\", e.expl_cb as \"numro d\'exemplaire\", e.expl_prix_achat  as \"prix\"\r\nFROM exemplaires e\r\nLEFT OUTER JOIN notices n ON e.expl_notice = n.notice_id\r\nORDER BY  n.tit1 ASC, e.expl_cb ASC','liste des exemplaires des jeux avec prix d\'achat.','12 10 11 1 13 9','',5,0,''),(36,'Emprunteurs / année','SELECT empr_nom as nom, empr_prenom as prnom, count(*)\r\nFROM empr e\r\nRIGHT OUTER JOIN pret_archive pa ON e.id_empr = pa.arc_id_empr\r\nWHERE YEAR(arc_debut) = !!param1!!\r\nGROUP BY empr_nom, empr_prenom\r\nORDER BY empr_nom, empr_prenom','Liste des emprunteurs ayant fait un emprunt au cours de l\'année citée. Attention, il faut que l\'option archivage_prets soit activée.','10 11 1 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date]]></ALIAS>\n  <TYPE>list</TYPE>\n<OPTIONS FOR=\"list\">\r\n <MULTIPLE>no</MULTIPLE>\r\n <ITEMS>\r\n  <ITEM VALUE=\"2010\">2010</ITEM>\r\n  <ITEM VALUE=\"2011\">2011</ITEM>\r\n  <ITEM VALUE=\"2012\">2012</ITEM>\r\n  <ITEM VALUE=\"2013\">2013</ITEM>\r\n  <ITEM VALUE=\"2014\">2014</ITEM>\r\n  <ITEM VALUE=\"2015\">2015</ITEM>\r\n  <ITEM VALUE=\"2016\">2016</ITEM>\r\n  <ITEM VALUE=\"2017\">2017</ITEM>\r\n  <ITEM VALUE=\"2018\">2018</ITEM>\r\n  <ITEM VALUE=\"2019\">2019</ITEM>\r\n  <ITEM VALUE=\"2020\">2020</ITEM>\r\n </ITEMS>\r\n <UNSELECT_ITEM VALUE=\"2009\">2009</UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',6,0,''),(37,'tous Exemplaires / statut','SELECT statut_libelle, expl_cb, tit1\r\nFROM notices, docs_statut, exemplaires\r\nWHERE expl_notice = notice_id\r\n  AND expl_statut = idstatut\r\nORDER BY  statut_libelle, expl_cb, tit1','Listes de tous les exemplaires en fonction de leurs statuts','12 10 11 1 13 9','',5,0,''),(38,'Jeux / support','SELECT DISTINCT section_libelle, tit1\r\nFROM notices, exemplaires, docs_section\r\nWHERE expl_notice = notice_id\r\n  AND expl_section = idsection\r\nORDER BY section_libelle, tit1','Liste des jeux en fonction de leur support','10 11 1 9','',5,0,''),(39,'50 jeux ','SELECT tit1, nbr\r\nfrom notices, nbr_jeux\r\nWHERE notice_id = id','Liste des 50 jeux les plus empruntés.','10 11 1 9','',7,0,''),(40,'Liste exemplaires / statut','select expl_cb as \"Code-barres\", CASE WHEN expl_notice>0 THEN concat(CASE WHEN serie_name IS NOT NULL THEN CONCAT(serie_name, \" : \") ELSE \"\" END, n1.tit1) ELSE CONCAT(n2.tit1,\" \",bulletin_numero,\" \") END as Titre from exemplaires left join notices n1 on expl_notice=n1.notice_id left join series on n1.tparent_id=serie_id left join bulletins on expl_bulletin=bulletin_id left join notices n2 on bulletin_notice=n2.notice_id where expl_statut=!!param1!! order by expl_cote','Liste paramétrée d\'exemplaires par statut ','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Statut]]></ALIAS>\n  <TYPE>query_list</TYPE>\n<OPTIONS FOR=\"query_list\">\r\n <QUERY><![CDATA[select idstatut,statut_libelle from docs_statut]]></QUERY>\r\n <MULTIPLE>no</MULTIPLE>\r\n <UNSELECT_ITEM VALUE=\"\"><![CDATA[Choisissez un statut]]></UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',5,0,''),(41,'Nombre total de prêts / date','SELECT date(arc_debut)as Date,count(*) as \"Nombre de prts\" FROM pret_archive where date(arc_debut) = \'!!param1!!\' group by 1','Nombe de prêt par rapport à une date.','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n</FIELDS>',7,0,''),(42,'Nombre total de prêts / date entre 2 dates','SELECT date(arc_debut)as Date,count(*) as \"Nombre de prts\" FROM pret_archive where date(arc_debut) >= \'!!param1!!\' and date(arc_debut) <= \'!!param2!!\'\r\ngroup by 1','Nombe de prêt par date entre deux dates','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date de début]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n <FIELD NAME=\"param2\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date de fin]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n</FIELDS>',7,0,''),(43,'exemplaires / supports','select expl_cb as \"Code-barres\", CASE WHEN expl_notice>0 THEN concat(CASE WHEN serie_name IS NOT NULL THEN CONCAT(serie_name, \" : \") ELSE \"\" END, n1.tit1) ELSE CONCAT(n2.tit1,\" \",bulletin_numero,\" \") END as Titre, IFNULL( ed_name, \'\' ) AS editeur, IFNULL( section_libelle, \'\' ) AS Section from exemplaires left join notices n1 LEFT JOIN publishers ON ed1_id = ed_id on expl_notice=n1.notice_id LEFT JOIN docs_section ON idsection = expl_section left join series on n1.tparent_id=serie_id left join bulletins on expl_bulletin=bulletin_id left join notices n2 on bulletin_notice=n2.notice_id where expl_typdoc=!!param1!! order by expl_cote','Liste d\'exemplaires par rapport au support','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[support]]></ALIAS>\n  <TYPE>query_list</TYPE>\n<OPTIONS FOR=\"query_list\">\r\n <QUERY><![CDATA[select idtyp_doc, tdoc_libelle from docs_type]]></QUERY>\r\n <MULTIPLE>no</MULTIPLE>\r\n <UNSELECT_ITEM VALUE=\"\"><![CDATA[Choisissez votre support]]></UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',5,0,''),(44,'Emprunteurs / date','SELECT empr_nom AS nom, empr_prenom AS prnom, pret_date AS date\r\nFROM `pret` , empr\r\nWHERE pret_date LIKE \'!!param1!!%\'\r\nAND id_empr = pret_idempr','liste des emprunteurs qui ont emprunté un jour donné','10 11 1 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[date]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS> \n </FIELD>\n</FIELDS>',6,0,''),(45,'Emprunteurs / date de retour de prêt','SELECT empr_nom AS nom, empr_prenom AS prnom, arc_fin AS date, tit1 as libell, expl_cb as \"rf.exempl.\"\r\nFROM pret_archive , empr, notices, exemplaires\r\nWHERE arc_fin LIKE \'!!param1!!%\'\r\nAND id_empr = arc_id_empr\r\nAND notice_id = arc_expl_notice\r\nAND expl_id = arc_expl_id\r\nORDER BY empr_nom asc, empr_prenom asc, tit1 asc','liste des emprunteurs qui ont rentré un jeu un jour donné','10 11 1 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Date]]></ALIAS>\n  <TYPE>date_box</TYPE>\n<OPTIONS FOR=\"date_box\"></OPTIONS>\n </FIELD>\n</FIELDS>',6,0,''),(46,'Liste alphabétique des emprunteurs','select empr_nom as nom, empr_prenom as prnom, empr_tel1 as tel1, empr_tel2 as tel2, empr_gsm as gsm\r\nfrom empr\r\norder by empr_nom ASC, empr_prenom ASC','','10 11 1 9','',6,0,''),(47,'Liste alphabétique des emprunteurs actifs','select empr_nom as nom, empr_prenom as prnom, empr_tel1 as tel1, empr_tel2 as tel2, empr_gsm as gsm, ct.libelle as \"code statistique\"\r\nfrom empr e, empr_statut s, empr_codestat ct\r\nwhere empr_statut = idstatut\r\n  and   statut_libelle = \"Actif\"\r\n  and   empr_codestat = ct.idcode\r\norder by ct.libelle ASC,empr_nom ASC, empr_prenom ASC','','10 11 1 9','',6,0,''),(48,'Liste des emprunteurs par catégorie','SELECT `empr_cb`as identifiant, empr_nom as nom, empr_prenom as prnom FROM `empr` WHERE empr_categ = !!param1!!','','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[catégorie]]></ALIAS>\n  <TYPE>query_list</TYPE>\n<OPTIONS FOR=\"query_list\">\r\n <QUERY><![CDATA[SELECT `id_categ_empr`, libelle FROM `empr_categ`]]></QUERY>\r\n <MULTIPLE>no</MULTIPLE>\r\n <UNSELECT_ITEM VALUE=\"\"><![CDATA[Choississez la catégorie]]></UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',6,0,''),(49,'Liste des emprunteurs par code statistique','SELECT `empr_cb` as identifiant, `empr_nom` as nom, `empr_prenom` as prnom FROM `empr` WHERE 	empr_codestat = !!param1!!','','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[code statistique]]></ALIAS>\n  <TYPE>query_list</TYPE>\n<OPTIONS FOR=\"query_list\">\r\n <QUERY><![CDATA[select idcode, libelle from empr_codestat]]></QUERY>\r\n <MULTIPLE>no</MULTIPLE>\r\n <UNSELECT_ITEM VALUE=\"\"><![CDATA[Choississez le code statistique]]></UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',6,0,''),(50,'Liste des emprunteurs par ordre de numéro','select empr_cb AS numro,empr_nom as nom, empr_prenom as prnom, empr_tel1 as tel1, empr_tel2 as tel2, empr_gsm as gsm\r\nfrom empr\r\nORDER BY LPAD( empr_cb, 10, \'0\' ) ASC','','12 10 11 1 13 9','',6,0,''),(51,'Listes des emprunteurs inscrits au cours d\'une année','SELECT empr_cb as numro, empr_nom AS nom, empr_prenom AS prnom, empr_date_adhesion AS date\r\nFROM empr\r\nWHERE YEAR(empr_date_adhesion) = !!param1!!\r\norder by empr_nom asc, empr_prenom asc','','10 11 1 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Année]]></ALIAS>\n  <TYPE>list</TYPE>\n<OPTIONS FOR=\"list\">\r\n <MULTIPLE>no</MULTIPLE>\r\n <ITEMS>\r\n  <ITEM VALUE=\"2010\"><![CDATA[2010]]></ITEM>\r\n  <ITEM VALUE=\"2011\"><![CDATA[2011]]></ITEM>\r\n  <ITEM VALUE=\"2012\"><![CDATA[2012]]></ITEM>\r\n  <ITEM VALUE=\"2013\"><![CDATA[2013]]></ITEM>\r\n  <ITEM VALUE=\"2014\"><![CDATA[2014]]></ITEM>\r\n  <ITEM VALUE=\"2015\"><![CDATA[2015]]></ITEM>\r\n  <ITEM VALUE=\"2016\"><![CDATA[2016]]></ITEM>\r\n  <ITEM VALUE=\"2017\"><![CDATA[2017]]></ITEM>\r\n  <ITEM VALUE=\"2018\"><![CDATA[2018]]></ITEM>\r\n  <ITEM VALUE=\"2019\"><![CDATA[2019]]></ITEM>\r\n  <ITEM VALUE=\"2020\"><![CDATA[2020]]></ITEM>\r\n </ITEMS>\r\n <UNSELECT_ITEM VALUE=\"2009\"><![CDATA[2009]]></UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',6,0,''),(52,'Nombre exemplaires / statut','select statut_libelle, count(*)\r\nfrom docs_statut, exemplaires\r\nwhere idstatut = expl_statut\r\ngroup by expl_statut','nombre d\'exemplaires par statut','12 10 11 1 13 9','',5,0,''),(53,'Nombre total d\'exemplaires','select count(*) as total\r\nfrom exemplaires','Nombre total d\'exemplairess','12 10 11 1 13 9','',5,0,''),(54,'Liste exemplaires / proprietaire','SELECT expl_cb as identifiant, tit1 as libell, ed_name as diteur, lender_libelle as propritaire\r\nFROM exemplaires, notices, publishers, lenders\r\nWHERE expl_notice = notice_id\r\nAND ed1_id = ed_id\r\nAND expl_owner = idlender\r\nAND expl_owner = !!param1!!\r\nORDER BY tit1 asc, expl_cb asc','','12 10 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Propriétaire]]></ALIAS>\n  <TYPE>query_list</TYPE>\n<OPTIONS FOR=\"query_list\">\r\n <QUERY><![CDATA[select idlender,lender_libelle from lenders]]></QUERY>\r\n <MULTIPLE>no</MULTIPLE>\r\n <UNSELECT_ITEM VALUE=\"\"><![CDATA[Choisissez un propriétaire]]></UNSELECT_ITEM>\r\n</OPTIONS>\n </FIELD>\n</FIELDS>',5,0,''),(55,'Liste des notices','select tit1 as nom_jeu, p.ed_name as editeur , n_contenu as contenu , count(*) as nombre_exemplaires\r\nfrom notices n\r\nleft join publishers p on p.ed_id=n.ed1_id\r\nleft join exemplaires e on e.expl_notice=n.notice_id\r\ngroup by nom_jeu, editeur, contenu\r\norder by nom_jeu, editeur\r\n\r\n','','12 10 11 1 13 9','',5,0,''),(56,'Liste des notices (Codes barres)','select tit1 as nom_jeu, p.ed_name as editeur , n_contenu as contenu, expl_cb as code_barres\r\nfrom notices n\r\nleft join publishers p on p.ed_id=n.ed1_id\r\nleft join exemplaires e on e.expl_notice=n.notice_id\r\norder by nom_jeu, editeur\r\n','','12 11 1 13 9','',5,0,''),(57,'Liste alphabétique de tous emprunteurs (+ adresse)','select empr_codestat as code_stat, empr_codestat.libelle as libelle_code_stat , empr_nom as nom, empr_prenom as prnom, empr_adr1, empr_adr2, empr_ville, empr_cp as code_postal, empr_pays, empr_tel1 as tel1, empr_tel2 as tel2, empr_gsm as gsm, empr_mail\r\nfrom empr\r\nleft outer join empr_codestat on  empr_codestat=empr_codestat.idcode \r\norder by empr_nom ASC, empr_prenom ASC','','12 11 1 13 9',NULL,6,0,''),(58,'Listes emprunteurs en cours','select empr_cb as \"CB\", empr_nom as \"Nom\", empr_prenom as \"Prnom\", empr_adr1 as \"Rue\", empr_cp as \"CP\", empr_ville as \"Ville\", empr_date_expiration as \"Expiration\", statut_libelle as \"Status\" \r\nfrom empr, empr_statut\r\nwhere idstatut = empr_statut\r\nand empr_date_expiration > now()\r\norder by empr_nom asc, empr_prenom asc','','12 11 1 13 9',NULL,6,0,''),(59,'Nombre d\'emprunteurs actifs par commune','select \r\nempr_cp as \'Commune\', count(*) as \'Nombre\'\r\nfrom empr e, empr_statut s\r\nwhere empr_statut = idstatut\r\n  and   statut_libelle = \"Actif\"\r\ngroup by empr_cp\r\norder by empr_cp ASC','nombre d\' emprunteurs avec le statut actif /commune','12 11 1 13 9',NULL,6,0,''),(68,'jeux ESAR + age min','SELECT DISTINCT section_libelle, tit1\r\nFROM notices, exemplaires, docs_section\r\nWHERE expl_notice = notice_id\r\n  AND expl_section = idsection\r\nORDER BY section_libelle, tit1','','12 11 1 13 9',NULL,5,0,''),(69,'Historique d\'un exemplaire d\'un jeu','SELECT expl_cb as \"numro d\'exemplaire\", tit1 as \"titre\", count(arc_expl_id) as \"Nombre de prt\" \r\nFROM pret_archive, exemplaires, notices \r\nWHERE notice_id = expl_notice \r\n  and expl_id = arc_expl_id \r\n  and expl_cb = \'!!param1!!\'\r\nGROUP BY  arc_expl_id ','Attention : cette requête calcule le nombre de prêt archivé. Il ne prend pas en compte les prêts en cours.','12 11 1 13 9','<?xml version=\"1.0\" encoding=\"iso-8859-1\"?>\n<FIELDS>\n <FIELD NAME=\"param1\" MANDATORY=\"yes\">\n  <ALIAS><![CDATA[Numéro de l\'exemplaire]]></ALIAS>\n  <TYPE>text</TYPE>\n<OPTIONS></OPTIONS>\n </FIELD>\n</FIELDS>',5,0,''),(70,'Nombre d\'exemplaires mis en circulation / année','SELECT count(expl